import client from"./sanity";const blogFields="\nnumber,\ntitle,\n'slug': slug.current,\ndate,\nsubtitle,\n'featuredImage': featuredImage.asset->url\n";export async function getAllBlogs(){const results=await client.fetch(`*[_type == "blog"]{${blogFields}}`);return results}export async function getAllBySlug(slug){const results=await client.fetch(`*[_type == "blog" && slug.current == $slug]{\n      ${blogFields}\n    }`,{slug:slug});return results[0]}